package in.co.variableAndOperators;

public class TernaryOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Ternary Operators ARE CONDITIONAL
		// if "a then x , else y"
		int a =30;
		int b=50;
		int c = (a>b)?a:b;
		System.out.println(c);
		
		
	}

}
