package in.co.basics;

public class BreakSatementExample_UnlabledType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i =0;
		boolean flag=true;
		
		while(flag) {
			//break the loop if i is eual to 5
		if(i==5) {
			break;
		}
		System.out.println(i+ " Hello Java");
		i++;
		}
	}

	}


