package in.co.mathClass;

public class Math_Class {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Mathematics operations");
		System.out.println("Max 2, 5 : "+Math.max(2, 5));
		//shows max value of two numbers
		
		System.out.println("Min 2, 5 : "+Math.min(2, 5));
		//shows min value of two numbers
		
		System.out.println("Absolute of 3.7 : "+Math.abs(-3.7));
		//shows positive of the number 
		
		System.out.println("Exp 10 : "+Math.exp(10));
		//shows result of : e^10.0
		
		System.out.println("Random Number : "+Math.random());
		//generate random number
		
		System.out.println("square root: "+Math.sqrt(4));
		//shows sqaure root of a number
		
	}

}
